import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import DefaultLayout from '../DefualtLayout'; // Corrected import
import { getAllCars } from '../../redux/Actions/carsActions';
import { Col, Row, Divider, DatePicker, Checkbox } from 'antd';
import { Link } from 'react-router-dom';
import Spinner from '../Spinner';
import './CarSection.css';

const { RangePicker } = DatePicker;

function CarSection() {
  const dispatch = useDispatch();

  // State for the cars data and error message
  const cars = useSelector((state) => state.cars.cars); // Adjusted selector
  const carErrorMessage = useSelector((state) => state.cars.errorMessage); // Adjusted selector

  // Fetch all cars on component mount
  useEffect(() => {
    dispatch(getAllCars());
  }, [dispatch]);

  // No need for a separate useEffect to set totalCars, you can directly use cars

  console.log(cars);
  console.log(carErrorMessage);

  return (
    <div className='Home'>
    <DefaultLayout>

    <Row justify='center' gutter={16}>

                        {totalCars.map(car => {
                            return <Col lg={5} sm={24} xs={24}>
                                <div className="car p-2 bs1">
                                    <img src={car.image} className="carimg" />

                                    <div className="car-content d-flex align-items-center justify-content-between">

                                        <div className='text-left pl-2'>
                                            <p>{car.name}</p>
                                            <p> Rent Per Hour {car.rentPerHour} /-</p>
                                        </div>

                                        <div>
                                            <button className="btn1 mr-2"><Link to={`/booking/${car._id}`}>Book Now</Link></button>
                                        </div>

                                    </div>
                                </div>
                            </Col>
                        })}

                    </Row>


    </DefaultLayout>


    </div>
  );
}

export default CarSection;
